import {Button, Container, Grid, Typography} from '@mui/material';
import React, {useEffect, useState} from 'react';
import {useLocation, useParams} from 'react-router-dom';
import {drawCards, getDeck, useGlobalState} from '../../helpers/CRUD/read';
import {useAppDispatch} from '../../redux-store/store';

const CurrentDeck = () => {
   const [gameOn, setGameOn] = useState(false);
   const {currentDeck} = useGlobalState();
   const params = useParams();
   const location = useLocation();
   const dispatch = useAppDispatch();
   useEffect(() => {
      dispatch(getDeck(params.id));
   }, []);
   return (
      <Container maxWidth="lg" sx={{marginTop: '5rem'}}>
         <Typography variant="h3">{currentDeck.deck_id}</Typography>

         <Typography variant="h3">
            Cards remaining in deck: {currentDeck.remaining}
         </Typography>
         {gameOn ? (
            <Button
               onClick={() => {
                  dispatch(drawCards(params.id));
                  setGameOn(true);
               }}
               variant="contained"
            >
               Play a game!
            </Button>
         ) : (
            <Grid container>
               <Grid item>
                  <img src={currentDraw.cards[0].images[0]}></img>
               </Grid>
               <Grid item></Grid>
               <Grid item></Grid>
            </Grid>
         )}
      </Container>
   );
};

export default CurrentDeck;
