import {Grid, Paper, Typography} from '@mui/material';
import React, {useEffect, useState} from 'react';
import {getDeck} from '../../helpers/CRUD/read';

const RenderDeck = ({deckId}: any) => {
   const [deck, setDeck] = useState({});
   useEffect(() => {
      setDeck(getDeck(deckId));
   }, []);
   return (
      <Grid item lg={3}>
         <Paper elevation={5} sx={{minHeight: '15rem', padding: '1rem'}}>
            <Typography variant="h6">Deck: {deckId}</Typography>
            <Typography variant="h6">
               Remaining cards in the deck: {deck.remaining}
            </Typography>
         </Paper>
      </Grid>
   );
};

export default RenderDeck;
