import {Button, Container, Grid, Typography} from '@mui/material';
import React, {useEffect, useState} from 'react';
import {useLocation, useParams} from 'react-router-dom';
import {calcHighestRank} from '../../helpers/calculate';
import {drawCards, getDeck, useGlobalState} from '../../helpers/CRUD/read';
import {useAppDispatch} from '../../redux-store/store';
import cardBack from './back_of_a_card.jpg';

const CurrentDeck = () => {
   const [gameOn, setGameOn] = useState(false);
   const [cardsShown, setCardsShown] = useState(false);
   const [buttonShown, setButtonShown] = useState(true);
   const [outcome, setOutcome] = useState({
      hasWon: 'undecided',
      chosenCard: 'none',
   });
   const {currentDeck, currentDraw} = useGlobalState();
   const params = useParams();
   const location = useLocation();
   const dispatch = useAppDispatch();
   useEffect(() => {
      dispatch(getDeck(params.id));
   }, []);

   function handleDraw(card: string) {
      console.log('card id', card);
      let winner = calcHighestRank(
         currentDraw.cards[0].code[0],
         currentDraw.cards[1].code[0]
      );

      if (card === winner) {
         setOutcome({hasWon: 'true', chosenCard: card});
      } else {
         setOutcome({hasWon: 'false', chosenCard: card});
      }
   }

   return (
      <Container maxWidth="lg" sx={{paddingTop: '6rem'}}>
         <Grid container sx={{alignItems: 'center'}}>
            <Grid
               item
               lg={4}
               sx={{
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  minHeight: '100vh',
               }}
            >
               {gameOn || (
                  <img
                     style={{
                        width: '226px',
                        height: '314px',
                        // cursor: 'pointer',
                     }}
                     src={cardBack}
                  />
               )}
               {gameOn && (
                  <>
                     {cardsShown ? (
                        <img
                           style={{
                              width: '226px',
                              height: '314px',
                              cursor: 'pointer',
                           }}
                           src={currentDraw.cards[0].images.png}
                        />
                     ) : (
                        <>
                           {currentDraw.success && (
                              <img
                                 id={currentDraw.cards[0].code[0]}
                                 style={{width: '226px', height: '314px'}}
                                 src={cardBack}
                                 onClick={e => {
                                    const target =
                                       e.target as HTMLButtonElement;
                                    console.log(target.id);
                                    handleDraw(target.id);
                                    setCardsShown(true);
                                    setButtonShown(true);
                                 }}
                              />
                           )}
                        </>
                     )}
                  </>
               )}
            </Grid>
            <Grid
               item
               lg={4}
               sx={{
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  justifyContent: 'space-evenly',
               }}
            >
               {buttonShown ? (
                  <>
                     <Typography variant="h6">
                        Deck id: {currentDeck.deck_id}
                     </Typography>
                     <Typography variant="h6">
                        Cards remaining in deck: {currentDeck.remaining}
                     </Typography>

                     {outcome.hasWon === 'true' && (
                        <Typography variant="h3" sx={{color: 'green'}}>
                           Congrats! You won!
                        </Typography>
                     )}
                     {outcome.hasWon === 'false' && (
                        <Typography variant="h3" sx={{color: 'red'}}>
                           Sorry, good luck next time
                        </Typography>
                     )}
                     <Button
                        onClick={() => {
                           //    console.log(currentDraw.cards[0].code[0]);
                           //    console.log(params.id);
                           dispatch(drawCards(params.id));
                           setGameOn(true);
                           setCardsShown(false);
                           setButtonShown(false);
                        }}
                        variant="contained"
                     >
                        Play!
                     </Button>
                  </>
               ) : (
                  <>
                     <Typography variant="h6">
                        Click on the left or right card
                     </Typography>
                  </>
               )}
            </Grid>
            <Grid
               item
               lg={4}
               sx={{
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
               }}
            >
               {gameOn || (
                  <img
                     style={{
                        width: '226px',
                        height: '314px',
                        // cursor: 'pointer',
                     }}
                     src={cardBack}
                  />
               )}
               {gameOn && (
                  <>
                     {cardsShown ? (
                        <img
                           style={{
                              width: '226px',
                              height: '314px',
                              cursor: 'pointer',
                           }}
                           src={currentDraw.cards[1].images.png}
                        />
                     ) : (
                        <>
                           {currentDraw.success && (
                              <img
                                 id={currentDraw.cards[1].code[0]}
                                 style={{width: '226px', height: '314px'}}
                                 src={cardBack}
                                 onClick={e => {
                                    const target =
                                       e.target as HTMLButtonElement;
                                    console.log(target.id);
                                    handleDraw(target.id);
                                    setCardsShown(true);
                                    setButtonShown(true);
                                 }}
                              />
                           )}
                        </>
                     )}
                  </>
               )}
            </Grid>
         </Grid>
      </Container>
   );
};

export default CurrentDeck;
