import {Container, Grid, IconButton, Typography} from '@mui/material';
import {Box} from '@mui/system';
import React from 'react';
import {Link, useNavigate} from 'react-router-dom';
import {addToDo} from '../helpers/CRUD/create';
import {useAppDispatch} from '../redux-store/store';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';

const Header = () => {
   const navigate = useNavigate();
   const dispatch = useAppDispatch();
   return (
      <Container maxWidth="lg">
         <Grid
            container
            sx={{
               display: 'flex',
               alignItems: 'center',
               justifyContent: 'space-between',
            }}
         >
            <Grid
               item
               sx={{
                  cursor: 'pointer',
                  fontSize: '3rem',
               }}
               onClick={() => {
                  navigate('/');
               }}
            >
               <Box
                  component="div"
                  sx={{
                     display: 'inline',
                     color: '#a855cc',
                     textShadow:
                        '-1px 1px 0 #000, 1px 1px 0 #000, 1px -1px 0 #000, -1px -1px 0 #000',
                  }}
               >
                  R
               </Box>
               <Box
                  component="div"
                  sx={{
                     display: 'inline',
                     color: '#a072c2',
                     textShadow:
                        '-1px 1px 0 #000, 1px 1px 0 #000, 1px -1px 0 #000, -1px -1px 0 #000',
                  }}
               >
                  A
               </Box>
               <Box
                  component="div"
                  sx={{
                     display: 'inline',
                     color: '#7979f7',
                     textShadow:
                        '-1px 1px 0 #000, 1px 1px 0 #000, 1px -1px 0 #000, -1px -1px 0 #000',
                  }}
               >
                  I
               </Box>
               <Box
                  component="div"
                  sx={{
                     display: 'inline',
                     color: '#78f378',
                     textShadow:
                        '-1px 1px 0 #000, 1px 1px 0 #000, 1px -1px 0 #000, -1px -1px 0 #000',
                  }}
               >
                  N
               </Box>
               <Box
                  component="div"
                  sx={{
                     display: 'inline',
                     color: '#ffff80',
                     textShadow:
                        '-1px 1px 0 #000, 1px 1px 0 #000, 1px -1px 0 #000, -1px -1px 0 #000',
                  }}
               >
                  B
               </Box>
               <Box
                  component="div"
                  sx={{
                     display: 'inline',
                     color: '#f5b779',
                     textShadow:
                        '-1px 1px 0 #000, 1px 1px 0 #000, 1px -1px 0 #000, -1px -1px 0 #000',
                  }}
               >
                  O
               </Box>
               <Box
                  component="div"
                  sx={{
                     display: 'inline',
                     color: '#f77c7c',
                     textShadow:
                        '-1px 1px 0 #000, 1px 1px 0 #000, 1px -1px 0 #000, -1px -1px 0 #000',
                  }}
               >
                  W
               </Box>
               <Box component="span" sx={{paddingLeft: '1rem'}}>
                  Lists
               </Box>
            </Grid>
            <Grid item>
               <IconButton
                  onClick={() => {
                     dispatch(addToDo());
                  }}
                  sx={{width: '100%'}}
               >
                  <AddCircleOutlineIcon />
               </IconButton>
               <Typography sx={{textAlign: 'center', marginBottom: '1rem'}}>
                  Add New Note
               </Typography>
            </Grid>
            <Grid item>
               <Link to="/about">About the app</Link>
            </Grid>
         </Grid>
      </Container>
   );
};

export default Header;
