import {ColorLensSharp} from '@mui/icons-material';
import {Grid, IconButton, Paper, Typography} from '@mui/material';
import React from 'react';
import AddToDoTask from './ToDoTask/AddToDoTask';
import DeleteIcon from '@mui/icons-material/Delete';

type toDoProps = {title: string; color: string};

const Todo = ({title, color}: toDoProps) => {
   return (
      <Grid item>
         <Paper
            elevation={5}
            sx={{
               width: '15rem',
               height: '15rem',
               backgroundColor: color,
               position: 'relative',
            }}
         >
            <Typography
               variant="h5"
               sx={{textAlign: 'center', marginBottom: '1rem'}}
            >
               {title}
            </Typography>
            <AddToDoTask></AddToDoTask>
            <IconButton sx={{position: 'absolute'}}>
               <DeleteIcon />
            </IconButton>
         </Paper>
      </Grid>
   );
};

export default Todo;
